<? if( $this->config->get( 'twittercard_status' ) == 1 ) { $this->document ->addMeta( 'twitter:card', 'summary' ) ->addMeta( 'twitter:domain', $this->url->link('common/home') ) ->addMeta( 'twitter:site', $this->config->get('twitter_site') ) ->addMeta( 'twitter:creator', $this->config->get('twitter_creator') ) ->addMeta( 'twitter:image', $this->model_tool_image->resize($manufacturer_info['image'], $this->config->get('config_image_category_width'), $this->config->get('config_image_category_height')) ) ->addMeta( 'twitter:description', str_replace( "\"", "&quot;", utf8_substr(trim(strip_tags( html_entity_decode($manufacturer_info['description'], ENT_QUOTES, 'UTF-8') ), " \t\n\r"), 0, 140) . '..' ) ) ->addMeta( 'twitter:title', $manufacturer_info['name'] ) ->addMeta( 'twitter:url', $this->url->link('product/manufacturer/info', 'manufacturer_id=' . $this->request->get['manufacturer_id'] . $url) ); } ?>