<? if( $this->config->get( 'twittercard_status' ) == 1 ) { $this->document ->addMeta( 'twitter:card', 'product' ) ->addMeta( 'twitter:domain', $this->url->link('common/home') ) ->addMeta( 'twitter:site', $this->config->get('twitter_site') ) ->addMeta( 'twitter:creator', $this->config->get('twitter_creator') ) ->addMeta( 'twitter:label1', 'Цена' ) ->addMeta( 'twitter:data1', $product_info['special'] ? $product_info['special'] : $product_info['price'] ) ->addMeta( 'twitter:image', $this->model_tool_image->resize( $product_info['image'], $this->config->get('config_image_popup_width'), $this->config->get('config_image_popup_height') ) ) ->addMeta( 'twitter:description', str_replace( "\"", "&quot;", utf8_substr(trim(strip_tags( html_entity_decode($product_info['description'], ENT_QUOTES, 'UTF-8') ), " \t\n\r"), 0, 140) . '..' ) ) ->addMeta( 'twitter:data2', $this->currency->getCode() ) ->addMeta( 'twitter:title', $product_info['name'] ) ->addMeta( 'twitter:label2', 'Валюта' ) ->addMeta( 'twitter:url', $this->url->link( 'product/product', 'product_id=' . $product_info['product_id'] ) ); } ?>